function updateNoteGroups(e){const t=document.querySelector("container-app"),o=t.shadowRoot.querySelector('container-note-group[group="archived"]'),n=t.shadowRoot.querySelector('container-note-group[group="other"]'),r=e.filter((e=>"true"===e.archived)).length,u=e.filter((e=>"false"===e.archived)).length;o.setAttribute("count",r),n.setAttribute("count",u)}function openModal(){const e=document.querySelector("container-app").shadowRoot.querySelector("content-modal"),t=document.querySelector("content-overlay");e.setAttribute("open","true"),t.setAttribute("is-modal-open","true")}function hideOverlay(){document.querySelector("content-overlay").setAttribute("is-modal-open","false")}document.addEventListener("DOMContentLoaded",(()=>{}));